jQuery(document).ready(function(){MELODYSCHOOL_STORAGE.media_frame=null;MELODYSCHOOL_STORAGE.media_link="";jQuery(".melodyschool_media_selector").on("click",function(a){melodyschool_show_media_manager(this);a.preventDefault();return false})});function melodyschool_show_media_manager(a){MELODYSCHOOL_STORAGE.media_link=jQuery(a);if(MELODYSCHOOL_STORAGE.media_frame){MELODYSCHOOL_STORAGE.media_frame.open();return false}MELODYSCHOOL_STORAGE.media_frame=wp.media({title:MELODYSCHOOL_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:MELODYSCHOOL_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:MELODYSCHOOL_STORAGE.media_link.data("update"),close:true}});MELODYSCHOOL_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+MELODYSCHOOL_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(MELODYSCHOOL_STORAGE.media_link.data("multiple")===true){MELODYSCHOOL_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=MELODYSCHOOL_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});MELODYSCHOOL_STORAGE.media_frame.open();return false};